<template>
    <div class="hyper-checkbox" :style="{ maxWidth: extend ? '' : '25px' }" :class="{ typeRight: type == 'right' }">
        <input type="checkbox" :disabled="disabled" :checked="modelValue" @change="$emit('update:modelValue', $event.target.checked)" />
        <div class="visual" :class="{ checked: modelValue, disabled: disabled }">
            <span class="check"></span>
        </div>
        <p class="label" :class="{ disabled: disabled }" @click="$emit('update:modelValue', !modelValue)">
            <slot />
        </p>
    </div>
</template>

<script>
export default {
    name: "HyCheckbox",
    props: {
        modelValue: {
            type: Boolean,
            required: true,
        },
        disabled: {
            type: Boolean,
            default: false,
        },
        extend: {
            type: Boolean,
            default: true,
        },
        type: {
            type: String,
            default: "left",
        },
    },
};
</script>

