        &.checked {
            background-color: var(--element-primary-bg-color);
            border-color: transparent;

            .check {
                opacity: 1;
            }
        }

        &.disabled {
            opacity: 50%;
        }

        .check {
            padding: 0;
            margin: 0;
            display: block;
            width: 13px;
            height: 6px;
            position: absolute;
            top: 43%;
            left: 50%;
            transform: translate(-50%, -50%) rotate(-45deg);
            border-bottom: 2px solid var(--element-secondary-bg-color);
            border-left: 2px solid var(--element-secondary-bg-color);
